String acctId = ''; // put your account Id here!
Integer nbrLoans = ; // put the number of loans you want to create here!

Account account = [SELECT Id FROM Account WHERE Id = :acctId];

List<LLC_BI__Loan__c> loans = new List<LLC_BI__Loan__c>();

for(Integer i = 0; i < nbrLoans; i++){
  loans.add(createLoan('Test Product Line 1', 'Test Product Type 1', 'Test Product 1', false, false, false, account));
}

nFORCE.DmlUtility.insertObj(loans);

public LLC_BI__Loan__c createLoan(
  String productLine,
  String productType,
  String product,
  Boolean createProductLine,
  Boolean createProductType,
  Boolean createProduct,
  Account account) {
    LLC_BI__Loan__c o = new LLC_BI__Loan__c();
    o.llc_bi__Account__c = account.Id;
    o.Name = 'TestLoan001';
    o.llc_bi__Product_Line__c = productLine;
    o.llc_bi__Product_Type__c = productType;
    o.llc_bi__Product__c = product;
    o.llc_bi__Amount__c = 10001;
    o.llc_bi__CloseDate__c = system.Today();
    o.llc_bi__Construction_CO_Date__c = system.Today();
      return o;
  }

Integer howManyYouWant = 8; // change this to the number you want
Id loanId = 'loanId'; // replace with the loan id you want to associate the debts to

List <LLC_BI__Debt__c> debtList = new List <LLC_BI__Debt__c>();
for(Integer i = 0; i < howManyYouWant; i++){
  LLC_BI__Debt__c debt = new LLC_BI__Debt__c();
  debt.LLC_BI__Available_Amount__c = (10000.99 * (i+1));
  debt.LLC_BI__Maturity_Date__c = Date.today();
  debt.LLC_BI__Original_Amount__c = (1299999.03 * (i+1));
  debt.LLC_BI__Principal_Balance__c = (300000.85 * (i+1));
  debt.LLC_BI__Exclude__c = false;
  debt.LLC_BI__New_Debt__c = false;
  debt.LLC_BI__New_Loan__c = false;
  debt.LLC_BI__Loan__c = loanId;
  debtList.add(debt);
}
nFORCE.DmlUtility.insertObj(debtList);
